import React, { useEffect, useRef } from "react";
import $ from "jquery";

const DataTable = (props) => {
  useEffect(() => {
    const table = $("#dataTable").DataTable({
      paging: true,
      lengthChange: false,
      searching: false,
      ordering: true,
      info: true,
      autoWidth: false,
      responsive: true,
    });
    return () => {
      table.destroy();
    };
  }, []);
  return (
    <div>
      <table
        id="dataTable"
        className="display"
        width="100%"
        ref={tableRef}
      ></table>
    </div>
  );
};

export default DataTable;
